const DEFAULT_IMAGE = 'default'; // Default image
const SIZE = 400; // Default image size, in pixels
const TYPE = 'mfg'; // Default image type

/**
 * Builds the product image url
 * ex: /io/client/mfg/diamondback/images/product/src/sc.400.400.bf/DB16-AIREN1-GREY-PROFILE.jpg
 * @param baseUrl
 * @param orgKey
 * @param fileName
 * @param orgImageType
 * @param imageSize
 */
const buildProductImageUrl = (baseUrl, orgKey, fileName, orgImageType = TYPE, imageSize = SIZE) => {
  const image = fileName && fileName.length ? fileName : DEFAULT_IMAGE;

  return `${baseUrl}/io/client/${orgImageType}/${orgKey}/images/product/src/sc.${
    imageSize}.${imageSize}.bf/${image}`;
};

/**
 * Builds the full product image url
 * ex: /io/client/mfg/diamondback/images/product/src/DB16-AIREN1-GREY-PROFILE.jpg
 * @param baseUrl
 * @param orgKey
 * @param fileName
 * @param orgImageType
 */
const buildProductImageUrlMaxSize = (baseUrl, orgKey, fileName, orgImageType = TYPE) => {
  const image = fileName && fileName.length ? fileName : DEFAULT_IMAGE;

  return `${baseUrl}/io/client/${orgImageType}/${orgKey}/images/product/src/${image}`;
};

export {
  buildProductImageUrl,
  buildProductImageUrlMaxSize,
};
