import React from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';

/**
 * Link that could be External if an href is specified
 */
const ExternalableLink = (props) => {
  const { children, href, to, ...rest } = props;

  if (href) {
    // This is an external link. Just use a standard anchor
    return (
      <a href={href} {...rest}>{children}</a>
    );
  }

  return (
    // This is an internal link. Use a Link to keep it in the router family
    <Link to={to} {...rest}>{children}</Link>
  );
};

ExternalableLink.propTypes = {
  children: PropTypes.node.isRequired,
  className: PropTypes.string,
  href: PropTypes.string,
  to: PropTypes.oneOfType([PropTypes.func, PropTypes.string]).isRequired,
};

export default ExternalableLink;
